				addOrUpdateFlag:false,
				leavedataCrossAddOrUpdateFlag: false,
				layouts: ["total","prev","pager","next","sizes"],
//导出excel
				json_fields: {
					"学号": "xuehao",    //常规字段
					"密码": "mima",    //常规字段
					"姓名": "xingming",    //常规字段
					"性别": "xingbie",    //常规字段
					"手机": "shouji",    //常规字段
